  select count(1) from cm_casedetail 
  
	
	 	project_id = #{testCase.projectId}
	
	
	  and version_id = #{testCase.versionId}
	
	
	  and case_name = #{testCase.caseName}
	
  	
  	 and case_title = #{testCase.caseTitle}
  	
  	
  	 and priority = #{testCase.priority}
  	
  	
  	 and module_id = #{testCase.moduleId}
  	
  	 and del_flag = '0'

  	select  from cm_casedetail
	  
	  	
	  		project_id = #{testCase.projectId}
	  	
	  	
	  	 and version_id = #{testCase.versionId}
	  	
	  	
	  	 and case_name = #{testCase.caseName}
	  	
	  	
	  	 and case_title = #{testCase.caseTitle}
	  	
	  	
	  	 and priority = #{testCase.priority}
	  	
	  	
	  	 and module_id = #{testCase.moduleId}
	  	
	  	 and del_flag = '0'
	    
	  limit #{pageRequest.offSet},#{pageRequest.pageSize}
